mapper.sql:
FROM chatbot_room r JOIN chatbot_member m USING(croom_num) LEFT OUTER JOIN (SELECT c_num,
REPLACE(REPLACE(REPLACE(message,'<','&lt;'),'>','&gt;'),'@{member}@','') message,
croom_regdate, croom_num FROM chatbot WHERE c_num IN (SELECT MAX(c_num) c_num FROM chatbot GROUP BY croom_num)) USING(croom_num) WHERE m.mem_num=#{mem_num}
