mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT b.*, r.re_cnt, f.fav_cnt FROM item b LEFT OUTER JOIN (SELECT count(*) re_cnt, item_num FROM review GROUP BY item_num)r ON b.item_num=r.item_num LEFT OUTER JOIN (SELECT count(*) fav_cnt, item_num FROM item_fav GROUP BY item_num)f ON b.item_num=f.item_num
)a)
WHERE rnum >= #{start} AND rnum <= #{end}
