mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT p.poll_num,
REPLACE(REPLACE(p.poll_title,'<','&lt;'),'>','&gt;') poll_title,
p.poll_content, p.poll_hit, p.poll_date, p.poll_deadline, p.poll_item1, p.poll_item2, p.poll_item3, p.poll_item4, p.mem_num, m.mem_id, m.mem_nickname FROM poll p JOIN member m ON p.mem_num = m.mem_num
)a)
WHERE rnum >= #{start} AND rnum <= #{end}
