mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT st_num,
REPLACE(REPLACE(st_phrase, '<', '&lt;'), '>', '&gt;') st_phrase,
s.st_photo1n, s.st_photo2n, s.st_photo3n, s.mem_num, s.st_regdate, s.st_mdate, s.item_num1, s.item_num2, s.item_num3, s.st_hide, m.mem_id, d.mem_photo, i1.item_brand i1_brand, i2.item_brand i2_brand, i3.item_brand i3_brand FROM style s LEFT OUTER JOIN member m ON s.mem_num = m.mem_num LEFT OUTER JOIN member_detail d ON m.mem_num = d.mem_num LEFT OUTER JOIN item i1 ON s.item_num1 = i1.item_num LEFT OUTER JOIN item i2 ON s.item_num2 = i2.item_num LEFT OUTER JOIN item i3 ON s.item_num3 = i3.item_num
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE style SET
st_phrase=#{st_phrase},
st_mdate=sysdate

	,item_num1=#{item_num1}


	,item_num1=null


	,item_num2=#{item_num2}


	,item_num2=null


	,item_num3=#{item_num3}


	,item_num3=null

WHERE st_num=#{st_num}
